import React, { useState } from "react";

function RecipeCreate({createRecipe}) {

  // TODO: When the form is submitted, a new recipe should be created, and the form contents cleared.
  // TODO: Add the required input and textarea form elements.
  // TODO: Add the required submit and change handlers

  const [name, setName] = useState("");
  const [cuisine, setCuisine] = useState("");
  const [photo, setPhoto] = useState("");
 // const [rating, setRating] = useState("");
  const [ingredients, setIngredients] = useState("");
  const [preparation, setPreparation] = useState("");

  const handleNameChange = (event) => setName(event.target.value);
  const handleCuisineChange = (event) => setCuisine(event.target.value);
  const handlePhotoChange = (event) => setPhoto(event.target.value);
  //const handleRatingChange = (event) => setRating(event.target.value);
  const handleIngredientsChange = (event) => setIngredients(event.target.value);
  const handlePreparationChange = (event) => setPreparation(event.target.value);


  const handleSubmit = (event) => {
    event.preventDefault();

    setName("");
    setCuisine("");
    setPhoto("");
    setIngredients("");
    setPreparation("");
  };



  return (
    <form name="create" onSubmit={handleSubmit}>
      <table>
        <tbody>
          <tr>
            <th>
              <label htmlFor="name">
                Name
                <td>
                  <input type="text" id="name" name="name" onChange={handleNameChange} value={name}/>
                </td>
              </label>            
            </th>

            <th>
              <label htmlFor="cusine">
                Cuisine
                <td>
                  <input type="text" id="cuisine" name="cuisine" onChange={handleCuisineChange} value={cuisine}/>
                </td>
              </label>
            </th>

            <th>
              <label htmlFor="photo">
                Photo
                <td>
                  <input type="text" id="photo" name="photo" onChange={handlePhotoChange} value={photo}/>
                </td>
              </label>
            </th>

            <th>
              <label htmlFor="ingredients">
                Ingredients
              <td>
                <textarea id="ingredients" name="ingredients" onChange={handleIngredientsChange} value={ingredients}/>
              </td>
              </label>
            </th>

            <th>
              <label htmlFor="preparation">
                Preparation
              <td>
                <textarea id="preparation" name="preparation" onChange={handlePreparationChange} value={preparation}/>
                </td>
              </label>
            </th>

            <th>
              Actions
            <td>
              <button type="submit">Create</button>
            </td>
            </th>
          </tr>
        </tbody>
      </table>
    </form>
  );
}

export default RecipeCreate;
